import React, {Components} from 'react';
import './App.css';

const list = [
  {
  title: 'React',
  url: 'https://reactjs.org/',
  author: 'Jordan Walke',
  num_comments: 3,
  points: 4,
  objectID: 0,
  },
  {
  title: 'Redux',
  url: 'https://redux.js.org/',
  author: 'Dan Abramov, Andrew Clark',
  num_comments: 2,
  points: 5,
  objectID: 1,
  },
  ];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      list: list
    };
  }
   render(){
    return (
      <div className="App">
        {this.state.list.map(item=>{
        return (
        <span>
          <h2 key={item.objectID}>
          {item.author} is a better user of {item.title}
          <span><p>You can reach them via their <a href={item.url}>website</a> henceforth</p></span> 
          </h2>
        </span>)
        })}
      </div> 
  );
   }

}

export default App;
