//import the redux guys, action and connecct
import {setSearchfiled} from '../action'
import {connect} from 'react-redux'
import React, {Component} from 'react';
import Personlist from '../components/Personlist'
import Scroll from '../components/Scroll'
import Searchbox from '../components/Searchbox'

//import {people} from './People'

const mapStateToProps= state=>

class App extends Component {
    constructor(){
        super()
        this.state={
            people: [],
            searchField:''
        }
    }

    componentDidMount(){

        fetch('https://jsonplaceholder.typicode.com/users')
        .then(response=>response.json())
        .then(users=>this.setState({people:users}))
    }   
    onSearchChange=(event)=>{
       this.setState({searchField:event.target.value})

    }
    render(){
        const filteredPeople=this.state.people.filter(people=>{
            return people.name.toLowerCase().includes(this.state.searchField.toLowerCase());

        })
        return (
            <div className='tc'>
                <div>
                    <h1 className=' georgia f2'>Passionate Moods</h1>
                </div>

                <Searchbox searchChange={this.onSearchChange}/>
                <Scroll>
                    <Personlist people={filteredPeople}/>
                </Scroll>
            </div>
    )
}

}



export default connect(mapStateToProps, mapDispatchToProps)(App);
