import React, {Component} from 'react';
import Personlist from './Personlist'
import Searchbox from './Searchbox'
import {people} from './People'


class App extends Component {
    constructor(){
        super()
        this.state={
            people:people,
            searchField:''
        }
    }
    onSearchChange=(event)=>{
       this.setState({searchField:event.target.value})

    }
    render(){
        const filteredPeople=this.state.people.filter(people=>{
            return people.name.toLowerCase().includes(this.state.searchField.toLowerCase());

        })
        return (
            <div className='tc light blue gradient reverse'>
                <div>
                    <h1 className='bg-light-purple georgia f2'>Passionate Moods</h1>
                </div>

                <Searchbox searchChange={this.onSearchChange}/>

                <Personlist people={filteredPeople}/>
            </div>
    )
}

}



export default App;
